public class bennefield8 {
    public static void main(String[] args) {
        String numberStr =
            "";

        int adjacentDigits = 13;
    	int maxProduct = 0;

        for (int i = 0; i <= numberStr.length() - adjacentDigits; i++) {
            int product = 1;
            for (int j = i; j < i + adjacentDigits; j++) {
                int digit = Character.getNumericValue(numberStr.charAt(j));
                product *= digit;
            }
            if (product > maxProduct) {
                maxProduct = product;
            }
        }

        System.out.println(maxProduct);
    }
}
